var Cylon = require('cylon');

Cylon.robot({
  connection: { name: 'arduino', adaptor: 'firmata', port: '/dev/ttyACM1' },
  devices: [
    { name: 'led', driver: 'led', pin: '13' },
    { name: 'button1', driver: 'makey-button', pin: '5' },
    { name: 'button2', driver: 'makey-button', pin: '6' },
    { name: 'button3', driver: 'makey-button', pin: '7' }
  ],

  work: function(my) {
    my.button1.on('push', function() {
      //my.led.toggle();
      console.log("5");
    });
    my.button2.on('push', function() {
      //my.led.toggle();
      console.log("6");
    });
    my.button3.on('push', function() {
      //my.led.toggle();
      console.log("7");
    });
  }
}).start();
