var Particle = require("../lib/particle");
var board = new Particle({
  token: process.env.PARTICLE_TOKEN,
  deviceId: process.env.PARTICLE_PHOTON_REDBOARD_1
});

board.on("ready", function() {
  console.log("CONNECTED");

  // this.setSamplingInterval(1000);
  console.log(this.productId);

  var values = {
    // Analog Read (ADC)
    A0: null,
    // Digital Read on an Analog Pin
    A1: null,
    // Digital Read
    D0: null,
  };

  this.pinMode("A0", this.MODES.ANALOG);
  // this.pinMode("A1", this.MODES.INPUT);
  // this.pinMode("D0", this.MODES.INPUT);

  this.analogRead("A0", function(data) {
    if (values.A0 !== data) {
      console.log("A0",  data);
    }
    values.A0 = data;
  });

  // this.digitalRead("A1", function(data) {
  //   if (values.A1 !== data) {
  //     console.log("A1",  data);
  //   }
  //   values.A1 = data;
  // });

  // this.digitalRead("D0", function(data) {
  //   if (values.D0 !== data) {
  //     console.log("D0",  data);
  //   }
  //   values.D0 = data;
  // });
});
