var Spark = require("../../spark-io");
var board = new Spark({
  token: process.env.SPARK_TOKEN,
  deviceId: process.env.SPARK_DEVICE_ID
});

board.on("ready", function() {
  console.log("CONNECTED");

  var a = "D0";
  var b = "D1";

  this.pinMode(a, this.MODES.SERVO);
  this.servoWrite(a, 100);

  this.pinMode(b, this.MODES.SERVO);
  this.servoWrite(b, 100);

  setTimeout(function() {
    this.us(a, 1900);
    this.us(b, 1900);
  }.bind(this), 1000);




});
